import React from 'react';
import { motion } from 'framer-motion';
import '../styles/TetrisGrid.css';

const items = Array.from({ length: 20 }, (_, i) => ({
  id: i,
  height: Math.floor(Math.random() * 3) + 1 // height multiplier (1-3)
}));

const TetrisGrid = () => {
  return (
    <section className="tetris-section" id="demo">
      <div className="tetris-grid">
        {items.map(item => (
          <motion.div
            key={item.id}
            className="tetris-box"
            style={{ gridRowEnd: `span ${item.height}` }}
            initial={{ opacity: 0, y: 100 }}
            whileInView={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.4, delay: item.id * 0.05 }}
            viewport={{ once: true }}
          >
            Box {item.id + 1}
          </motion.div>
        ))}
      </div>
    </section>
  );
};

export default TetrisGrid;
